DROP VIEW IF EXISTS dba.test_failed_focus;CREATE OR REPLACE VIEW dba.test_failed_focus ASSELECT-- test_result tr source table-- test_case  tc  required parent table	tr.test_passed,	tc.object_class,	tc.object_schema,	tc.object_name,	tc.function_signature,	tc.test_name,	tc.comparison_options,	tr.diagnostics,	tr.summary,	tc.test_code,    tc.object_schema = 'IB_not_Postgres' as is_an_ib_test,	tc.expected_response,	tr.actual_response,	tc.expected_error,	tr.actual_error,	tc.expected_outcome,	tr.actual_outcomeFROM test_result    AS trLEFT JOIN test_case AS tc ON (tc.id = tr.test_case_id)WHERE test_passed = false;ALTER VIEW dba.test_failed_focus	OWNER TO user_change_structure;-------------------------------------------------- Register view------------------------------------------------CALL view_register ('dba','test_failed_focus','Finds failed test results, and strips down the output to the most releavant fields.');