DROP VIEW IF EXISTS dba.test_result_plus;CREATE OR REPLACE VIEW dba.test_result_plus ASSELECT-- test_result tr source table-- test_case  tc  required parent table-- patch_log  tl  optional parent table	tr.test_passed,	tc.object_class,	tc.object_schema,	tc.object_name,	tc.function_signature,	tc.test_name,	tr.id,	tr.result_dts,	tr.server_address,	tr.database_name,	tr.pg_version,	tr.user_name,	tr.diagnostics,	tr.summary,	tc.test_code,    tc.object_schema = 'IB_not_Postgres' as is_an_ib_test,	tc.rds_only,	tc.modifies_data,	tc.expected_response,	tr.actual_response,	tc.expected_error,	tr.actual_error,	tc.expected_outcome,	tr.actual_outcome,    tc.id as test_case_id,	tc.response_type,	tc.comparison_options,	tc.setup_code,	tc.teardown_codeFROM test_result    AS trLEFT JOIN test_case AS tc ON (tc.id = tr.test_case_id)LEFT JOIN patch_log AS pl ON (pl.id = tr.patch_log_id);ALTER VIEW dba.test_result_plus	OWNER TO user_change_structure;-------------------------------------------------- Register view------------------------------------------------CALL view_register ('dba','test_result_plus','test_result data, with lookup values.');