DROP VIEW IF EXISTS dba.view_list;CREATE OR REPLACE VIEW dba.view_list AS    SELECT schemaname::citext AS schema_name,   		   viewname::citext   AS view_name,   		   viewowner::citext  AS view_owner      FROM pg_views     WHERE schemaname NOT IN ('pg_catalog','information_schema','extensions')  ORDER BY viewname;ALTER VIEW dba.view_list	OWNER TO user_change_structure;-------------------------------------------------- Register view------------------------------------------------CALL view_register ('dba','view_list','Lists all of our views, even ''plumbing'' views in types_plus used for INSERTs.');