CREATE OR REPLACE VIEW ascendco.need_history_plus AS   SELECT need_history.id,          need_history.item_id,          need_history.facility_id,          need_history.hsys_id,          item.name_ AS item_name,          facility.name_ AS facility_name,          hsys.name_ AS hsys_name,          need_history.perc_down,          need_history.total_qty,          need_history.sterile_qty,          need_history.available_qty,          need_history.still_need_qty,          need_history.usage_,          need_history.is_low_flow,          need_history.need_for_case,          need_history.status,          created_dts,          deleted_dts,          duration_seconds,          created_local_dts     FROM need_historyLEFT JOIN item     ON (item.id     = need_history.item_id)   LEFT JOIN facility ON (facility.id = need_history.facility_id)   LEFT JOIN hsys     ON (hsys.id     = need_history.hsys_id); ALTER TABLE ascendco.need_history_plus    OWNER TO user_change_structure;