CREATE OR REPLACE VIEW ascendco.need_list AS   SELECT facility.name_ AS facility_name,          hsys.name_ AS hsys_name,          item.name_ AS item_name,                    need.item_priority,                    need.perc_down AS percent_down,          need.total_qty AS qty_total,          need.sterile_qty AS qty_sterile,          need.available_qty,          need.still_need_qty,          need.usage_ AS flow,          need.is_low_flow     FROM needLEFT JOIN item     ON (item.id     = need.item_id)   LEFT JOIN facility ON (facility.id = need.facility_id)   LEFT JOIN hsys     ON (hsys.id     = need.hsys_id); ALTER TABLE ascendco.need_list    OWNER TO user_change_structure;